import './Board.css';
import React from 'react';
import { useState, useEffect } from 'react';
import { Squares } from './square.js';
import io from 'socket.io-client';

const socket = io();

export function Board(props) {

  const [board, setBoard] = useState([Array(9).fill(" ")]);
  const [sateO, setStateO] = useState(1);

  function onClickButton(i) {
    if(board.length == 9){
      return;
    }
    const newboard = [...board]
    if(sateO == 0){
      newboard[i] = 'O';
      setStateO(sateO+1);
      setBoard(newboard)
      console.log(newboard);
    }
    else{
      newboard[i] = 'X';
      //const newboard = [...board]
      setStateO(0);
      setBoard(newboard)
      console.log(newboard);
    }

    //socket.emit('clicked', { tile : i, value : newboard[i] });
    socket.emit('clicked', { tile : i});
  };

  useEffect(() => {
    // Listening for a chat event emitted by the server. If received, we
    // run the code in the function that is passed in as the second arg
    socket.on('clicked', (data) => {
      console.log('Board is updating');
      //console.log(data.tile + " : " + data.value);
      // If the server sends a message (on behalf of another client), then we
      // add it to the list of messages to render it on the UI.
      //setBoard(board[data.tile] = data.value);
      setBoard(prevBoard => [...prevBoard[data] = "X"]);
      //prevMessages => [...prevMessages, data.message])
    });
  }, []);

  return (
    <div>
      <Squares onClickButton={onClickButton} val={board} />
    </div>
  );
}
