import logo from './logo.svg';
import './App.css';
import { Board } from './Board.js';
import { useState } from 'react';

function App() {
  const [board, setBoard] = useState([Array(9).fill(null)]);
  const [sateO, setStateO] = useState(1);

  function onClickButton(i) {
    const newboard = [...board]
    if(sateO == 0){
      newboard[i] = 'O';
      setStateO(sateO+1);
      setBoard(newboard)
      console.log(newboard);
    }
    else{
      newboard[i] = 'X';
      //const newboard = [...board]
      setStateO(0);
      setBoard(newboard)
      console.log(newboard);
    }
  };

  return (
    <div>
      <Board onClickButton={onClickButton} val={board} />
    </div>
  );
}
export default App;
