import './Board.css';
import React from 'react';
import { useState, useEffect } from 'react';
import { Squares } from './square.js';
import io from 'socket.io-client';

const socket = io();

export function Board(props) {

  const [board, setBoard] = useState([Array(9).fill(" ")]);
  const [sateO, setStateO] = useState(1);

  function onClickButton(i) {
    if(board.length == 9 || board[i] == 'X'|'O'){
      return;
    }
    //const newboard = [...board]
    if(sateO == 0){
      board[i] = 'O';
      setStateO(sateO+1);
      setBoard(board)
      //console.log(newboard);
    }
    else{
      board[i] = 'X';
      //const newboard = [...board]
      setStateO(0);
      setBoard(board)
      //console.log(newboard);
    }

    //socket.emit('clicked', { tile : i, value : newboard[i] });
    socket.emit('clicked', { data : board});
  };

  useEffect(() => {
    socket.on('clicked', (data) => {
      console.log("Old board: " + data.board);
      console.log('Board is updating ');
      //console.log("tile: " + data.tile);
      /*
      if(sateO == 0){
        board[data.tile] = 'O';
        setStateO(sateO+1);
        console.log("Assigning: " + newboard);
      }
      if(sateO == 1){
        newboard[data.tile] = 'X';
        setStateO(0);
        console.log("Assigning: " + newboard);
      }*/
      //setBoard(prevBoard => [...board]);
      console.log("New borad: " + board);
      setBoard(board => [...board]);
      //prevMessages => [...prevMessages, data.message])
    });
  }, []);

  return (
    <div>
      {board.map((index) => <Squares onClickButton={onClickButton} val={board[index]} />)}
    </div>
  );
}
