import logo from './logo.svg';
import './App.css';
import { Board } from './Board.js';
import { useState } from 'react';

var nextO = false;
function App() {
  const [board, setBoard] = useState([Array(9).fill(null)]);

  function onClickButton(i) {
    const newboard = [...board]
    if(nextO == true){
      newboard[i] = 'O';
      nextO = false;
      setBoard(newboard)
      console.log(newboard);
    }
    else{
      newboard[i] = 'X';
      //const newboard = [...board]
      nextO = true;
      setBoard(newboard)
      console.log(newboard);
    }
  };

  return (
    <div>
      <Board onClickButton={onClickButton} val={board} />
    </div>
  );
}
export default App;
