import './Board.css';
import React from 'react';
import { useState, useEffect } from 'react';
import { Squares } from './square.js';
import io from 'socket.io-client';

const socket = io();

export function Board(props) {

  const [board, setBoard] = useState([Array(9).fill(" ")]);
  const [sateO, setStateO] = useState(1);

  function onClickButton(i) {
    if(board.length == 9 || board[i] == 'X'|'O'){
      return;
    }
    if(sateO == 0){
      board[i] = 'O';
      setStateO(sateO+1);
      setBoard(board)
      console.log(board);
      socket.emit('clicked', {board: board, index : i, val : board[i]});
      console.log("After emit borad: " + board);
    }
    if(sateO == 1){
      board[i] = 'X';
      setStateO(0);
      setBoard(board)
      console.log(board);
      //socket.emit('clicked', {board: board, index : i, val : board[i]});
      console.log("After emit borad: " + board);
    }

  };

  useEffect(() => {
    socket.on('clicked', (data) => {
      console.log("Old board: " + data.board);
      console.log('Board is updating ');
      console.log("tile: " + data.index);

      data.board[data.index] = data.val;

      setBoard(data.board);
      console.log("New borad: " + board);
    });
  }, []);

  return (
    <div>
      <Squares onClickButton={onClickButton} val={board} />
    </div>
  );
}
