import './Board.css';
import React from 'react';
import { useState, useEffect } from 'react';
import { Squares } from './square.js';
import io from 'socket.io-client';

const socket = io();

export function Board(props) {

  const [board, setBoard] = useState([Array(9).fill(" ")]);
  const [sateO, setStateO] = useState(1);

  function onClickButton(i) {
    if(board.length == 9 || board[i] == 'X'|'O'){
      return;
    }
    //const newboard = [...board]
    if(sateO == 0){
      board[i] = 'O';
      setStateO(sateO+1);
      setBoard(board)
      console.log(board);
      socket.emit('clicked', { index : i, val : board[i]});
      return;
    }
    else{
      board[i] = 'X';
      //const newboard = [...board]
      setStateO(0);
      setBoard(board)
      console.log(board);
      socket.emit('clicked', { index : i, val : board[i]});
      return;
    }

    //socket.emit('clicked', { tile : i, value : newboard[i] });
    //socket.emit('clicked', { index : i, val : board[i]});
  };

  console.log("Outside the function board: " + board);

  useEffect(() => {
    socket.on('clicked', (data) => {
      console.log("Old board: " + board);
      console.log('Board is updating ');
      console.log("tile: " + data.index);

      board[data.index] = data.val;

      setBoard(prevBoard => [...prevBoard + board]);
      //setBoard(board);
      console.log("New borad: " + board);
    });
  }, []);

  return (
    <div>
      <Squares onClickButton={onClickButton} val={board} />
    </div>
  );
}
