import './Board.css';
import React from 'react';
import { useState, useEffect, useRef } from 'react';
import { Squares } from './square.js';
import { ListItem } from './ListItem.js';
import io from 'socket.io-client';

const socket = io();
export function Board(props) {

  const [board, setBoard] = useState([" "]);
  const [stateO, setStateO] = useState(0);
  const [player, setPlayer] = useState([]);
  const [spectator, setSpectator] = useState([]);
  const inputRef = useRef(null);
  var currentUser = null;

  function assigneUser(){
    if(inputRef != null){
      const inp = inputRef.current.value;
      currentUser = inp;
      console.log("user input: " + inp);

      socket.emit('assigne', {val: inp});
    }
  }


  function calculateWinner(squares) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
        return squares[a];
      }
    }
  return null;
  }

function check(){
  var i
  for (i = 0; i < 2; i++){
    if(player[i] == currentUser){
      return true;
    }
    else{
      continue
    }
  }
  return false;
}

function turns(){

}


  function onClickButton(i) {

    if(check()){
      return;
    }

    if(board[i] == 'X' || board[i] == 'O'){
      return;
    }

    if(stateO == 1){
      board[i] = 'O';
      setStateO((prevStateO) => prevStateO - 1);
    }

    if(stateO == 0){
      board[i] = 'X';
      setStateO((prevStateO) => prevStateO + 1);
    }

    const newboard = [...board];
    setBoard(newboard);

    /*if(calculateWinner(board)){
      alert("You Won!!!");
      setBoard([]);
    }*/

    socket.emit('clicked', {board: newboard, val: newboard[i]});
    return newboard;
  };

  useEffect(() => {

    socket.on('assigne_players', (server_players) => {
      const pl = [...server_players];
      setPlayer(pl);
    })

    socket.on('assigne_spectator', (server_spectator) => {
      const sp = [...server_spectator];
      setSpectator(sp);
    })

    socket.on('clicked', (data) => {
      //console.log("Before useEffect borad: " + board);
      //console.log("Before useEffect sateO: " + stateO);

      const newboard = [...data.board];
      if(data.val == 'X'){
        setStateO(1);
      }
      else{
        setStateO(0);
      }
      setBoard(newboard);

      //console.log("After useEffect borad: " + newboard);
      //console.log("After useEffect sateO: " + stateO);
    });

  }, []);

  return (
    <div>
      Enter your username: <input ref={inputRef} type='text' />
      <button onClick={assigneUser}>Submit</button>

      <div class="right">
      Players:
        <ol>
          {player.map(users => <ListItem name={users} />)}
        </ol>
      </div>

      <div class="right1">
      Spectator:
        <ol>
          {spectator.map(users => <ListItem name={users} />)}
        </ol>
      </div>

      <Squares onClickButton={onClickButton} val={board} />
    </div>

  );
}
