import './Board.css';
import React from 'react';
import { useState, useEffect } from 'react';
import { Squares } from './square.js';
import io from 'socket.io-client';

const socket = io();

export function Board(props) {

  const [board, setBoard] = useState([Array(9).fill(" ")]);
  const [sateO, setStateO] = useState(1);

  function onClickButton(i) {
    if(board.length == 9 || board[i] == 'X'|'Y'){
      return;
    }
    const newboard = [...board]
    if(sateO == 0){
      newboard[i] = 'O';
      setStateO(sateO+1);
      setBoard(newboard)
      console.log(newboard);
    }
    else{
      newboard[i] = 'X';
      //const newboard = [...board]
      setStateO(0);
      setBoard(newboard)
      console.log(newboard);
    }

    //socket.emit('clicked', { tile : i, value : newboard[i] });
    socket.emit('clicked', { tile : i});
  };

  useEffect(() => {
    socket.on('clicked', (data) => {
      console.log('Board is updating ');
      console.log("tile: " + data.tile);

      if(sateO == 0){
        board[data.tile] = "O";
        setStateO(sateO+1);
      }
      else{
        board[data.tile] = "X";
        setStateO(0);
      }
      console.log(board);
      setBoard(board => [...board]);
      //prevMessages => [...prevMessages, data.message])
    });
  }, []);

  return (
    <div>
      <Squares onClickButton={onClickButton} val={board} />
    </div>
  );
}
