import './Board.css';
import React from 'react';
import { useState, useEffect } from 'react';
import { Squares } from './square.js';
import io from 'socket.io-client';

const socket = io();

export function Board(props) {

  const [board, setBoard] = useState([Array(9).fill(null)]);
  const [sateO, setStateO] = useState(1);

  function onClickButton(i) {
    console.log("Before onclick function borad: " + board);
    /*
    if(board.length == 9 || board[i] == 'X'|'O'){
      return;
    }
    if(sateO == 0){
      board[i] = 'O';
      setStateO((prevStateO) => prevStateO + 1);
      setBoard(prevBoard => ([...prevBoard, ...board]));
      socket.emit('clicked', {index : i, val : board[i]});
    }
    if(sateO == 1){
      board[i] = 'X';
      setStateO(0);
      setBoard(prevBoard => ([...prevBoard, ...board]));
      socket.emit('clicked', {index : i, val : board[i]});
    }
    */
    const newboard = [...board];
    newboard[i] = 'X';
    setBoard(newboard);
    socket.emit('clicked', {index : i, val : newboard[i]});
    console.log("After onclick function borad: " + newboard);

  };

  useEffect(() => {
    console.log("Before useEffect borad: " + board);
    socket.on('clicked', (data) => {
      const newboard = [...board];
      newboard[data.index] = data.val;
      setBoard(board);
      //setBoard(prevBoard => ([...prevBoard, ...newboard]));
      //board.map((item, index) => index === data.index ? data.val : item);
      console.log("After useEffect borad: " + newboard);
    });
  }, []);

  return (
    <div>
      <Squares onClickButton={onClickButton} val={board} />
    </div>
  );
}
